import { Component, OnInit } from '@angular/core';
import { IonicModule } from '@ionic/angular';
import { IonHeader, IonToolbar, IonTitle, IonContent } from '@ionic/angular/standalone';
import { CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';  
import { CommonModule } from '@angular/common';
import { MusicService } from '../services/music';
import { StorageService } from '../services/storage';
import { Router } from '@angular/router'; 
import { albums } from 'ionicons/icons';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
  imports: [IonHeader, IonToolbar, IonTitle, IonContent, CommonModule ],
  schemas: [CUSTOM_ELEMENTS_SCHEMA]
})

export class HomePage implements OnInit {
  //color base
  colorClaro = 'var(--color-claro)';
  colorOscuro = 'var(--color-oscuro)';
  //colores del slide
  colorSlideTitle = 'var(--color-slide-title)';
  colorSlideTexto = 'var(--color-texto-slide)';
  //estados actuales
  colorActual = this.colorOscuro;
  temaActual = this.colorSlideTitle;


  //[actividad 1 (lista)] 
  //[actividad 2 (lista)]
  genres =  [
    {
      title: "Musica para chambear",
      image: "data:image/jpeg;base64,",
      description: "Para chambear (trabajar) de forma productiva, lo más recomendable es la música instrumental o con letras neutras para evitar distracciones."
    },
    {
      title: "Salsa",
      image: "data:image/png;base64,",
      description: "género musical vibrante y bailable que condensa una rica fusión de ritmos e influencias afrocaribeñas y latinoamericanas"
    },
    {
      title: "Vallenato",
      image: "data:image/jpeg;base64,",
      description: "uno de los géneros musicales más emblemáticos y queridos de Colombia, originario de la región Caribe, específicamente del Valle de Upar (Valledupar)."
    },
  ]
  tracks: any;
  albums: any;

  constructor(
    private storageService: StorageService, private musicService: MusicService,
    private router: Router) {}

  async ngOnInit(){
    this.loadAlbums();
    this.loadTracks();
    await this.loadStorageData();
    this.simularCargaDatos(); 
  }

  loadTracks(){
    this.musicService.getTracks().then(tracks =>{
      this.tracks = tracks;
      console.log(this.tracks, "las canciones")
    })
  }
  loadAlbums(){
    this.musicService.getAlmbuns().then(albums =>{
      this.albums = albums;
      console.log(this.albums, "los albums")
    })
  }


  async cambiarColor() {
    //if ternario
   this.colorActual = this.colorActual === this.colorOscuro ? this.colorClaro : this.colorOscuro
   await this.storageService.set('theme', this.colorActual)
   console.log('tema Guardado:', this.colorActual )


  }
  cambiarTema() {
  this.temaActual =
    this.temaActual === this.colorSlideTitle
      ? this.colorSlideTexto
      : this.colorSlideTitle;
}

async loadStorageData(){
   const savedTheme = await this.storageService.get('Theme')
   if (savedTheme) {
    this.colorActual = savedTheme
   }
}

async simularCargaDatos() {
  const data = await this.obtenerDatoSimulados();
  console.log('Datos simulados: ', data)
}

obtenerDatoSimulados(){
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve (['Rock', 'Vallenato', 'Trap'])
    }, 1500)
  })
}

// Crear una funcion para ir a ver la intro se va a conectar con una funcion que debemos agregar en el html y al hacer click ejecute esta funcion para llevarme  a ver la intro  [LISTA]
verIntro() {
    this.router.navigateByUrl('/intro');
  }
}